import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  ncinta: string;
  nsocior: string;
  fecha: string;

  constructor(private http: HttpClient) { }

  onSubmit(form: NgForm) {
    const formData = {
      cinta: this.ncinta,
      socio: this.nsocior,
      fecha: this.fecha
    };

    this.http.post('http://localhost:5000/api/ficha/prestamo', formData)
      .subscribe(
        response => {
          console.log('¡Datos enviados con éxito!');
          // Realiza aquí cualquier otra acción después de enviar los datos

          // Reinicia el formulario
          form.reset();
        },
        error => {
          console.error('Error al enviar los datos:', error);
          // Realiza aquí cualquier otra acción en caso de error
        }
      );
  }
}
<div class="container">
  <h1>Prestamo</h1>

  <form #prestamoForm="ngForm" (ngSubmit)="onSubmit(prestamoForm)">
    <div class="form-group">
      <label for="movie-name">Numero de la cinta:</label>
      <input type="text" id="ncinta" name="ncinta" placeholder="Ingrese el nombre de la película" [(ngModel)]="ncinta" required>
    </div>
    <div class="form-group">
      <label for="director">Nombre del socio:</label>
      <input type="text" id="nsocior" name="nsocior" placeholder="Ingrese el nombre del director" [(ngModel)]="nsocior" required>
    </div>
    <div class="form-group">
      <label for="actor">Fecha en que se lleva la cinta:</label>
      <input type="date" id="fecha" name="fecha" [(ngModel)]="fecha" required>
    </div>

    <button type="submit" [disabled]="!prestamoForm.form.valid">Enviar</button>
  </form>
</div>
