import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-cintas-prestadas',
  templateUrl: './cintas-prestadas.component.html',
  styleUrls: ['./cintas-prestadas.component.css']
})
export class CintasPrestadasComponent implements OnInit {
  fichas: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.obtenerFichasPrestamo();
  }

  obtenerFichasPrestamo() {
    this.http.get<any[]>('http://localhost:5000/api/ficha/prestamo').subscribe(
      (response) => {
        this.fichas = this.generarCodigosCinta(response);
      },
      (error) => {
        console.log(error);
      }
    );
  }

  generarCodigosCinta(fichas: any[]): any[] {
    return fichas.map((ficha, index) => ({
      ...ficha,
      codigoCinta: `C${index + 1}`
    }));
  }
}
