import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})

@NgModule({
  declarations: [
    PrincipalComponent
  ],
  imports: [
    CommonModule,
    FormsModule // Agrega FormsModule al arreglo de importaciones
  ]
})

export class PrincipalComponent {
  cintasDisponibles: any[] = [];
  socioSeleccionado: string = '';
  sociosDisponibles: any[] = [];
  cintaSeleccionada: string = '';

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.obtenerCintasDisponibles();
    this.obtenerSociosDisponibles();
  }

  obtenerCintasDisponibles() {
    this.http.get<any>('http://localhost:5000/api/cinta').subscribe(
      response => {
        this.cintasDisponibles = response.cintasDisponibles;
      },
      error => {
        console.error(error);
      }
    );
  }

  obtenerSociosDisponibles() {
    this.http.get<any>('http://localhost:5000/api/socio').subscribe(
      response => {
        this.sociosDisponibles = response.sociosDisponibles;
      },
      error => {
        console.error(error);
      }
    );
  }

  enviarFormulario() {
    console.log(this.cintaSeleccionada);
    console.log(this.socioSeleccionado);
    // Aquí puedes realizar la lógica adicional con los IDs de la cinta y el socio seleccionados
  }
}
