import { Component, OnInit } from '@angular/core';
import { jsPDF } from 'jspdf';
import { SociosService } from './socios.service';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent implements OnInit {
  socios: any[];

  constructor(private sociosService: SociosService) {}

  ngOnInit() {
    this.cargarSocios();
  }

  cargarSocios() {
    this.sociosService.getSocios().subscribe(
      response => {
        this.socios = response;
      },
      error => {
        console.error('Error al cargar los socios:', error);
      }
    );
  }

  generarReporte() {
    const doc = new jsPDF();
    let y = 10;

    this.socios.forEach(socio => {
      doc.text('Nombre: ' + socio.nombre, 10, y);
      doc.text('Dirección: ' + socio.direccion, 10, y + 10);
      doc.text('Teléfono: ' + socio.telefono, 10, y + 20);
      doc.text('Directores favoritos: ' + socio.directoresFavoritos.join(', '), 10, y + 30);
      doc.text('Actores favoritos: ' + socio.actoresFavoritos.join(', '), 10, y + 40);
      doc.text('Géneros favoritos: ' + socio.generosFavoritos.join(', '), 10, y + 50);
      y += 60;
    });

    doc.save('listado-socios.pdf');
  }
}
