import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  cinta: string;
  socio: string;
  fecha: string;

  constructor(private http: HttpClient) {}

  enviarFormulario(event: Event) {
    event.preventDefault(); // Evitar el comportamiento de envío predeterminado del formulario
    
    // Realizar la solicitud POST a la API
    this.http.post('http://localhost:5000/api/ficha/prestamo', {
      cinta: this.cinta,
      socio: this.socio,
      fecha: this.fecha
    }).subscribe(
      (response) => {
        console.log(response);
        // Realizar cualquier acción adicional después de enviar el formulario
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Prestamo</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h1>Prestamo</h1>
    <form id="prestamo-form">
      <div class="form-group">
        <label for="movie-name">Numero de la cinta:</label>
        <input type="text" id="ncinta" name="cinta" placeholder="Ingrese el nombre de la película">
      </div>
      <div class="form-group">
        <label for="director">Nombre del socio:</label>
        <input type="text" id="nsocior" name="socio" placeholder="Ingrese el nombre del director">
      </div>
      <div class="form-group">
        <label for="actor">Fecha en que se lleva la cinta:</label>
        <input type="date" id="fecha" name="fecha" placeholder="Ingrese el nombre del actor" value="por defecto el plazo es de una semana">
      </div>
      
      <button type="submit">Enviar</button>
    </form>
  </div>
</body>
</html>
