import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent implements OnInit {
  constructor(private http: HttpClient, private router: Router) { }

  ngOnInit() { }

  onSubmit(form: any) {
    const formData = {
      cinta: form.ncinta,
      socio: form.nsocior,
      fecha: form.fecha
    };

    this.http.post<any>('http://localhost:5000/api/ficha/prestamo', formData).subscribe(
      (response) => {
        console.log('Préstamo realizado:', response);
        this.router.navigate(['/peliculas-prestadas']);
      },
      (error) => {
        console.log('Error al realizar el préstamo:', error);
      }
    );
  }
}
