import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {

}
<div class="crud-table">
  <h2 class="title">Películas Prestadas</h2>
  <table class="table">
    <thead>
      <tr>
        <th>Nombre de la Película</th>
        <th>Actores</th>
        <th>Género</th>
        <th>Director</th>
      </tr>
    </thead>
    <tbody id="peliculas-table-body">
    </tbody>
  </table>
</div>

<script>
  $(document).ready(function() {
    // Hacer la solicitud GET para obtener los datos de las películas
    $.get('http://localhost:5000/api/pelicula/', function(data) {
      var peliculas = data; // Los datos de las películas recibidos del servidor

      // Recorrer las películas y agregarlas a la tabla
      peliculas.forEach(function(pelicula) {
        var row = $('<tr>');
        row.append($('<td>').text(pelicula.nombre));
        row.append($('<td>').text(pelicula.reparto.join(', ')));
        row.append($('<td>').text(pelicula.genero));
        row.append($('<td>').text(pelicula.director));

        $('#peliculas-table-body').append(row);
      });
    });
  });
</script>
