import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  constructor(private http: HttpClient, private router: Router) {}

  enviarPrestamo() {
    // Obtener los datos del formulario
    const ncinta = (<HTMLInputElement>document.getElementById('ncinta')).value;
    const nsocior = (<HTMLInputElement>document.getElementById('nsocior')).value;
    const fecha = (<HTMLInputElement>document.getElementById('fecha')).value;

    // Realizar la llamada a la API para guardar el préstamo
    this.http.post('http://localhost:5000/api/ficha/prestamo', {
      cinta: ncinta,
      socio: nsocior,
      fecha: fecha
    }).subscribe(
      (response) => {
        console.log(response);
        // Redirigir al usuario a la página de "Películas Prestadas"
        this.router.navigate(['/contact']);
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Prestamo</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h1>Prestamo</h1>
    <form id="prestamo-form" (ngSubmit)="enviarPrestamo()">
      <div class="form-group">
        <label for="movie-name">Numero de la cinta:</label>
        <input type="text" id="ncinta" name="cinta" placeholder="Ingrese el nombre de la película" [(ngModel)]="cinta">
      </div>
      <div class="form-group">
        <label for="director">Nombre del socio:</label>
        <input type="text" id="nsocior" name="socio" placeholder="Ingrese el nombre del director" [(ngModel)]="socio">
      </div>
      <div class="form-group">
        <label for="actor">Fecha en que se lleva la cinta:</label>
        <input type="date" id="fecha" name="fecha" placeholder="Ingrese el nombre del actor" value="por defecto el plazo es de una semana" [(ngModel)]="fecha">
      </div>
        
      <button type="submit">Enviar</button>
    </form>
  </div>
  
</body>
</html>
