import { Component } from '@angular/core';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent {

}
<!DOCTYPE html>
<html>
<head>
  <title>Listado de Socios</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h2 style="text-align: center;">Socios</h2>
    <table id="socios-table" class="table crud-table">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Dirección</th>
          <th>Teléfono</th>
          <th>Directores favoritos</th>
          <th>Actores favoritos</th>
          <th>Géneros favoritos</th>
        </tr>
      </thead>
      <tbody>
      </tbody>
    </table>
    <div style="text-align: center;">
      <button id="reporte-button">Reporte</button>
    </div>
  </div>

  <script>
    $(document).ready(function() {
      // Hacer la solicitud GET para obtener los datos de los socios
      $.get('http://localhost:5000/api/socio/', function(data) {
        var socios = data; // Los datos de los socios recibidos del servidor

        // Recorrer los socios y agregarlos a la tabla
        socios.forEach(function(socio) {
          var row = $('<tr>');
          row.append($('<td>').text(socio.nombre));
          row.append($('<td>').text(socio.direccion));
          row.append($('<td>').text(socio.telefono));
          row.append($('<td>').text(socio.directores_favoritos.join(', ')));
          row.append($('<td>').text(socio.actores_favoritos.join(', ')));
          row.append($('<td>').text(socio.generos_favoritos.join(', ')));

          $('#socios-table tbody').append(row);
        });
      });

      // Agregar el evento de clic al botón "Reporte"
      $('#reporte-button').click(function() {
        generarReporte();
      });

      // Función para generar el reporte
      function generarReporte() {
        // Hacer la solicitud GET para obtener los PDF
        $.get('http://localhost:5000/api/pdf/', function(response) {
          // El servidor devuelve los PDF en la respuesta

          // Aquí puedes realizar la acción deseada con los PDF, como abrirlos en una nueva ventana o descargarlos

          console.log('Se han obtenido los PDF');
        });
      }
    });
  </script>
</body>
</html>
