import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { jsPDF } from 'jspdf';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent implements OnInit {

  socios: any[] = []; // Variable para almacenar los socios obtenidos

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.obtenerSocios();
  }

  obtenerSocios() {
    this.http.get<any[]>('http://localhost:5000/api/pdf/').subscribe(
      (response) => {
        const parser = new DOMParser();
        const html = parser.parseFromString(response, 'text/html');
        const tableRows = html.getElementsByTagName('tr');

        for (let i = 1; i < tableRows.length; i++) {
          const tableData = tableRows[i].getElementsByTagName('td');
          const socio = {
            nombre: tableData[0].innerText,
            direccion: tableData[1].innerText,
            telefono: tableData[2].innerText,
            directoresFavoritos: tableData[3].innerText,
            actoresFavoritos: tableData[4].innerText,
            generosFavoritos: tableData[5].innerText
          };
          this.socios.push(socio);
        }
      },
      (error) => {
        console.log(error);
      }
    );
  }

  generarReporte() {
    const doc = new jsPDF();
    doc.text('Contenido del reporte PDF', 10, 10);
    doc.save('reporte.pdf');
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Listado de Socios</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.0/jspdf.umd.min.js"></script>
</head>
<body>
  <div class="container">
    <h2 style="text-align: center;">Socios</h2>
    <table id="socios-table" class="table crud-table">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Dirección</th>
          <th>Teléfono</th>
          <th>Directores favoritos</th>
          <th>Actores favoritos</th>
          <th>Géneros favoritos</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let socio of socios">
          <td>{{ socio.nombre }}</td>
          <td>{{ socio.direccion }}</td>
          <td>{{ socio.telefono }}</td>
          <td>{{ socio.directoresFavoritos }}</td>
          <td>{{ socio.actoresFavoritos }}</td>
          <td>{{ socio.generosFavoritos }}</td>
        </tr>
      </tbody>
    </table>
    <div style="text-align: center;">
      <button (click)="generarReporte()" class="button-estilo">Reporte</button>
    </div>
  </div>
</body>
</html>
