import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  ncinta: string;
  nsocior: string;
  fecha: string;

  constructor(private http: HttpClient) { }

  enviarFormulario() {
    const formData = {
      cinta: this.ncinta,
      socio: this.nsocior,
      fecha: this.fecha
    };

    this.http.post('http://localhost:5000/api/ficha/prestamo', formData)
      .subscribe(
        response => {
          console.log('Solicitud enviada correctamente', response);
          // Aquí puedes agregar el código adicional que deseas ejecutar después de enviar el formulario
        },
        error => {
          console.error('Error al enviar la solicitud', error);
          // Aquí puedes manejar el error de acuerdo a tus necesidades
        }
      );
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Prestamo</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h1>Prestamo</h1>
    <form id="prestamo-form" (submit)="enviarFormulario()"></form>
    <form id="prestamo-form">
      <div class="form-group">
        <label for="movie-name">Numero de la cinta:</label>
        <input type="text" id="ncinta" name="cinta" placeholder="Ingrese el nombre de la película">
      </div>
      <div class="form-group">
        <label for="director">Nombre del socio:</label>
        <input type="text" id="nsocior" name="socio" placeholder="Ingrese el nombre del director">
      </div>
      <div class="form-group">
        <label for="actor">Fecha en que se lleva la cinta:</label>
        <input type="date" id="fecha" name="fecha" placeholder="Ingrese el nombre del actor" value="por defecto el plazo es de una semana">
      </div>
      
      <button type="submit">Enviar</button>
    </form>
  </div>
</body>
</html>
