import { Component } from '@angular/core';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent {

}
<!DOCTYPE html>
<html>
<head>
  <title>Listado de Socios</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.0/jspdf.umd.min.js"></script>
</head>
<body>
  <div class="container">
    <h2 style="text-align: center;">Socios</h2>
    <table id="socios-table" class="table crud-table">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Dirección</th>
          <th>Teléfono</th>
          <th>Directores favoritos</th>
          <th>Actores favoritos</th>
          <th>Géneros favoritos</th>
        </tr>
      </thead>
      <tbody>
      </tbody>
    </table>
    <div style="text-align: center;">
      <button id="reporte-button">Reporte</button>
    </div>
  </div>

  <script>
    $(document).ready(function() {
      $("#reporte-button").click(function() {
        // Realizar una solicitud POST a la API
        $.ajax({
          url: "http://localhost:5000/api/pdf",
          method: "POST",
          success: function(response) {
            console.log("Reporte generado exitosamente.");
            // Aquí puedes agregar el código para manejar la respuesta de la API, como mostrar un mensaje de éxito al usuario.
          },
          error: function(xhr, status, error) {
            console.error("Error al generar el reporte:", error);
            // Aquí puedes agregar el código para manejar el error, como mostrar un mensaje de error al usuario.
          }
        });
      });
    });
  </script>
</body>
</html>
