import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { jsPDF } from 'jspdf';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent implements OnInit {

  socios: any[] = [];
 // Variable para almacenar los socios obtenidos

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.obtenerSocios();
  }

  obtenerSocios() {
    this.http.get<any[]>('http://localhost:5000/api/socio/').subscribe(
      (response) => {
        this.socios = response; // Almacena los socios en la variable socios
      },
      (error) => {
        console.log(error);
      }
    );
  }

  generarReporte() {
    const doc = new jsPDF();
    doc.text('Contenido del reporte PDF', 10, 10);
    doc.save('reporte.pdf');
  }
}
