import { Component } from '@angular/core';
import { jsPDF } from 'jspdf';

@Component({
  selector: 'app-socios',
  templateUrl: './socios.component.html',
  styleUrls: ['./socios.component.css']
})
export class SociosComponent {
  generarReporte() {
    const doc = new jsPDF();
    doc.text('Contenido del reporte PDF', 10, 10);
    doc.save('laboratorio14-reporte.pdf');
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Listado de Socios</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.0/jspdf.umd.min.js"></script>

</head>
<body>
  <div class="container">
    <h2 style="text-align: center;">Socios</h2>
    <table id="socios-table" class="table crud-table">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Dirección</th>
          <th>Teléfono</th>
          <th>Directores favoritos</th>
          <th>Actores favoritos</th>
          <th>Géneros favoritos</th>
        </tr>
      </thead>
      <tbody id="socios-table-body">
      </tbody>
    </table>
    <div style="text-align: center;">
      <button onclick="generarReporte()" class="button-estilo">Reporte</button>
    </div>
  </div>

  <script>
    function cargarSocios() {
      // Realizar una solicitud GET a la API para obtener los socios
      $.ajax({
        url: 'http://localhost:5000/api/socio',
        type: 'GET',
        success: function(response) {
          mostrarSocios(response);
        },
        error: function(error) {
          console.error('Error al cargar los socios:', error);
        }
      });
    }

    function mostrarSocios(socios) {
      const tableBody = document.getElementById('socios-table-body');

      socios.forEach(function(socio) {
        const row = document.createElement('tr');

        const nombreCell = document.createElement('td');
        nombreCell.textContent = socio.nombre;
        row.appendChild(nombreCell);

        const direccionCell = document.createElement('td');
        direccionCell.textContent = socio.direccion;
        row.appendChild(direccionCell);

        const telefonoCell = document.createElement('td');
        telefonoCell.textContent = socio.telefono;
        row.appendChild(telefonoCell);

        const directoresFavoritosCell = document.createElement('td');
        directoresFavoritosCell.textContent = socio.directoresFavoritos.join(', ');
        row.appendChild(directoresFavoritosCell);

        const actoresFavoritosCell = document.createElement('td');
        actoresFavoritosCell.textContent = socio.actoresFavoritos.join(', ');
        row.appendChild(actoresFavoritosCell);

        const generosFavoritosCell = document.createElement('td');
        generosFavoritosCell.textContent = socio.generosFavoritos.join(', ');
        row.appendChild(generosFavoritosCell);

        tableBody.appendChild(row);
      });
    }

    function generarReporte() {
      // Realizar una solicitud GET a la API para obtener los socios
      $.ajax({
        url: 'http://localhost:5000/api/socio',
        type: 'GET',
        success: function(response) {
          const doc = new jsPDF();
          let y = 10;

          response.forEach(function(socio) {
            doc.text('Nombre: ' + socio.nombre, 10, y);
            doc.text('Dirección: ' + socio.direccion, 10, y + 10);
            doc.text('Teléfono: ' + socio.telefono, 10, y + 20);
            doc.text('Directores favoritos: ' + socio.directoresFavoritos.join(', '), 10, y + 30);
            doc.text('Actores favoritos: ' + socio.actoresFavoritos.join(', '), 10, y + 40);
            doc.text('Géneros favoritos: ' + socio.generosFavoritos.join(', '), 10, y + 50);
            y += 60;
          });

          doc.save('listado-socios.pdf');
        },
        error: function(error) {
          console.error('Error al generar el reporte:', error);
        }
      });
    }

    $(document).ready(function() {
      cargarSocios();
    });
  </script>
</body>
</html>
