import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-cintas-prestadas',
  templateUrl: './contact.component.html',
  styleUrls: ['./cintas-prestadas.component.css']
})
export class CintasPrestadasComponent implements OnInit {
  fichas: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.obtenerFichasPrestamo();
  }

  obtenerFichasPrestamo() {
    this.http.get<any[]>('http://localhost:5000/api/ficha/prestamo').subscribe(
      (response) => {
        this.fichas = response;
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<div class="crud-table">
  <h2 class="title">Cintas Prestadas</h2>
  <table class="table">
    <thead>
      <tr>
        <th>Nº de Cinta</th>
        <th>Socio</th>
        <th>Fecha de Prestamo</th>
        <th>Fecha de Devolución</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let ficha of fichas">
        <td>{{ ficha.nroCinta }}</td>
        <td>{{ ficha.socio }}</td>
        <td>{{ ficha.fechaPrestamo }}</td>
        <td>{{ ficha.fechaDevolucion }}</td>
      </tr>
    </tbody>
  </table>
</div>
