import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {

}
<!DOCTYPE html>
<html>
<head>
  <title>Prestamo</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h1>Prestamo</h1>
    <form id="prestamo-form">
      <div class="form-group">
        <label for="movie-name">Numero de la cinta:</label>
        <input type="text" id="ncinta" name="cinta" placeholder="Ingrese el nombre de la película">
      </div>
      <div class="form-group">
        <label for="director">Nombre del socio:</label>
        <input type="text" id="nsocior" name="socio" placeholder="Ingrese el nombre del director">
      </div>
      <div class="form-group">
        <label for="actor">Fecha en que se lleva la cinta:</label>
        <input type="text" id="fecha" name="fecha" placeholder="Ingrese el nombre del actor" value="por defecto el plazo es de una semana">
      </div>
      
      <button type="submit">Enviar</button>
    </form>
  </div>

  <script>
    $(document).ready(function() {
      $('#prestamo-form').submit(function(event) {
        event.preventDefault(); // Evita que se envíe el formulario de manera tradicional

        // Obtener los valores de los campos de entrada
        var ncinta = $('#ncinta').val();
        var nsocior = $('#nsocior').val();
        var fecha = $('#fecha').val();

        // Realizar la solicitud POST para guardar la ficha de préstamo
        $.ajax({
          url: 'http://localhost:5000/api/ficha/prestamo',
          type: 'POST',
          dataType: 'json',
          data: {
            "cinta": ncinta,
            "socio": nsocior,
            "fecha": fecha
          },
          success: function(response) {
            // Manejar la respuesta del servidor en caso de éxito
            console.log('Ficha de préstamo guardada:', response);

            // Realizar la solicitud GET para obtener los archivos PDF
            $.get('http://localhost:5000/api/pdf/', function(data) {
              // Manejar los archivos PDF recibidos del servidor
              console.log('Archivos PDF obtenidos:', data);
            });
          },
          error: function(error) {
            // Manejar errores en caso de fallo en la solicitud POST
            console.error('Error al guardar la ficha de préstamo:', error);
          }
        });
      });
    });
  </script>
</body>
</html>
