import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {
  ncinta: '';
  nsocior: ;
  fecha: string;

  constructor(private http: HttpClient) { }

  enviarFormulario() {
    const formData = {
      cinta: this.ncinta,
      socio: this.nsocior,
      fecha: this.fecha
    };

    this.http.post('http://localhost:5000/api/ficha/prestamo', formData)
      .subscribe(
        response => {
          console.log('Solicitud enviada correctamente', response);
          // Aquí puedes agregar el código adicional que deseas ejecutar después de enviar el formulario
        },
        error => {
          console.error('Error al enviar la solicitud', error);
          // Aquí puedes manejar el error de acuerdo a tus necesidades
        }
      );
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Prestamo</title>
  <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</head>
<body>
  <div class="container">
    <h1>Prestamo</h1>
    <form id="prestamo-form">
      <div class="form-group">
        <label for="movie-name">Numero de la cinta:</label>
        <input type="text" [(ngModel)]="ncinta" id="ncinta" name="cinta" placeholder="Ingrese el nombre de la película">
      </div>
      <div class="form-group">
        <label for="director">Nombre del socio:</label>
        <input type="text" [(ngModel)]="nsocior" id="nsocior" name="socio" placeholder="Ingrese el nombre del socio">
      </div>
      <div class="form-group">
        <label for="actor">Fecha en que se lleva la cinta:</label>
        <input type="date" [(ngModel)]="fecha" id="fecha" name="fecha" placeholder="Ingrese la fecha">
      </div>
      
      <button type="button" (click)="enviarFormulario()">Enviar</button>
    </form>
  </div>
  
</body>
</html>
